import React, { useState, useEffect } from 'react';

interface CarouselSlide {
  id: number;
  title: string;
  subtitle: string;
  description: string;
  image: string;
  gradient: string;
}

const HeroCarousel: React.FC = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isPlaying, setIsPlaying] = useState(true);

  const slides: CarouselSlide[] = [
    {
      id: 1,
      title: "Leading LED Display",
      subtitle: "Solutions",
      description: "We specialize in high-quality LED displays, digital signage, and visual solutions that transform spaces and captivate audiences. From indoor displays to outdoor billboards, we deliver cutting-edge technology with exceptional service.",
      image: "LED Display Preview",
      gradient: "from-blue-900 to-blue-700"
    },
    {
      id: 2,
      title: "Perfect Reality",
      subtitle: "for Your Business",
      description: "Experience stunning visual clarity with our advanced LED technology. From retail environments to corporate spaces, our displays create immersive experiences that engage and inspire.",
      image: "Digital Signage",
      gradient: "from-gray-900 to-gray-700"
    },
    {
      id: 3,
      title: "Innovative",
      subtitle: "Visual Solutions",
      description: "Transform any space with our cutting-edge display technology. From interactive installations to large-scale outdoor displays, we bring your vision to life with unmatched quality and reliability.",
      image: "Interactive Displays",
      gradient: "from-purple-900 to-purple-700"
    }
  ];

  useEffect(() => {
    if (!isPlaying) return;

    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [isPlaying, slides.length]);

  const goToSlide = (index: number) => {
    setCurrentSlide(index);
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  return (
    <section className="relative bg-gradient-to-r from-blue-900 to-blue-700 text-white overflow-hidden">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-24">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
          {/* Left Section - Text Content */}
          <div className="relative z-10">
            <h1 className="text-5xl lg:text-6xl font-bold mb-6 leading-tight">
              {slides[currentSlide].title}
              <span className="block text-blue-200">{slides[currentSlide].subtitle}</span>
            </h1>
            <p className="text-xl mb-8 text-blue-100 leading-relaxed">
              {slides[currentSlide].description}
            </p>
            <div className="flex flex-col sm:flex-row gap-4">
              <button className="bg-white text-blue-900 px-8 py-4 rounded-lg font-semibold hover:bg-blue-50 transition-colors duration-200">
                Explore Our Services
              </button>
              <button className="border-2 border-white text-white px-8 py-4 rounded-lg font-semibold hover:bg-white hover:text-blue-900 transition-colors duration-200">
                View Projects
              </button>
            </div>
          </div>

          {/* Right Section - Visual Display */}
          <div className="relative">
            <div className={`bg-gradient-to-br ${slides[currentSlide].gradient} rounded-2xl p-8 shadow-2xl transition-all duration-500`}>
              <div className="aspect-video bg-blue-300 rounded-lg flex items-center justify-center">
                <span className="text-blue-900 font-semibold">{slides[currentSlide].image}</span>
              </div>
            </div>
          </div>
        </div>

        {/* Navigation Controls */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex items-center space-x-4">
          {/* Progress Indicators */}
          <div className="flex space-x-2">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => goToSlide(index)}
                className={`w-3 h-1 rounded-full transition-all duration-300 ${
                  index === currentSlide ? 'bg-white' : 'bg-white/30'
                }`}
              />
            ))}
          </div>

          {/* Play/Pause Button */}
          <button
            onClick={togglePlayPause}
            className="w-10 h-10 rounded-full border border-white/30 flex items-center justify-center hover:bg-white/10 transition-colors duration-200"
          >
            {isPlaying ? (
              <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M6 4h4v16H6V4zm8 0h4v16h-4V4z"/>
              </svg>
            ) : (
              <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M8 5v14l11-7z"/>
              </svg>
            )}
          </button>
        </div>

        {/* Navigation Arrows */}
        <button
          onClick={prevSlide}
          className="absolute left-4 top-1/2 transform -translate-y-1/2 w-12 h-12 rounded-full border border-white/30 flex items-center justify-center hover:bg-white/10 transition-colors duration-200"
        >
          <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>

        <button
          onClick={nextSlide}
          className="absolute right-4 top-1/2 transform -translate-y-1/2 w-12 h-12 rounded-full border border-white/30 flex items-center justify-center hover:bg-white/10 transition-colors duration-200"
        >
          <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
        </button>
      </div>

      {/* Slide Transition Overlay */}
      <div className="absolute inset-0 pointer-events-none">
        {slides.map((slide, index) => (
          <div
            key={slide.id}
            className={`absolute inset-0 transition-opacity duration-500 ${
              index === currentSlide ? 'opacity-100' : 'opacity-0'
            }`}
          >
            <div className={`absolute inset-0 bg-gradient-to-r ${slide.gradient}`} />
          </div>
        ))}
      </div>
    </section>
  );
};

export default HeroCarousel;
