import React, { useState, useEffect } from 'react';

const slides = [
  {
    headline: (
      <>
        Where Vision <br /> Meets Innovation
      </>
    ),
    subheadline: 'Experience stunning visual clarity with our advanced LED technology. From retail environments to corporate spaces, our displays create immersive experiences that engage and inspire.',
    accent: '‚ú®',
    gradient: 'from-blue-500 to-purple-600'
  },
  {
    headline: (
      <>
        See What's Possible <br /> With Every Pixel.
      </>
    ),
    subheadline: 'Discover the power of every pixel. Our LED displays redefine what\'s possible, delivering vibrant visuals and unmatched performance for any space.',
    accent: 'üåü',
    gradient: 'from-green-500 to-blue-600'
  },
  {
    headline: (
      <>
        More Than Screens.<br /> It's an Experience.
      </>
    ),
    subheadline: 'Step into a world where technology and creativity merge. Our solutions go beyond screens‚Äîthey create unforgettable experiences.',
    accent: 'üöÄ',
    gradient: 'from-orange-500 to-red-600'
  }
];

const HeroSection: React.FC = () => {
  const [current, setCurrent] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      if (!isTransitioning) {
        setIsTransitioning(true);
        setTimeout(() => {
          setCurrent((prev) => (prev + 1) % slides.length);
          setIsTransitioning(false);
        }, 300);
      }
    }, 5000);
    return () => clearInterval(interval);
  }, [isTransitioning]);

  const handleSlideChange = (index: number) => {
    if (index !== current && !isTransitioning) {
      setIsTransitioning(true);
      setTimeout(() => {
        setCurrent(index);
        setIsTransitioning(false);
      }, 300);
    }
  };

  return (
    <section className="relative bg-[#67595e] text-white overflow-hidden" style={{ width: '1423px', height: '503px' }}>
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-0 left-0 w-96 h-96 bg-gradient-to-br from-white/5 to-transparent rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-0 right-0 w-80 h-80 bg-gradient-to-tl from-white/5 to-transparent rounded-full blur-3xl animate-pulse delay-1000"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-gradient-to-r from-white/3 to-transparent rounded-full blur-2xl animate-spin-slow"></div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-20 h-full relative z-10">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center h-full">
          {/* Left Section - Text Content */}
          <div className="relative z-20">
            {/* Accent Icon */}
            <div className={`inline-flex items-center justify-center w-16 h-16 bg-gradient-to-br ${slides[current].gradient} rounded-2xl mb-6 transform transition-all duration-700 ${isTransitioning ? 'scale-0 rotate-180' : 'scale-100 rotate-0'}`}>
              <span className="text-2xl">{slides[current].accent}</span>
            </div>

            {/* Headline with Advanced Animation */}
            <div className="relative overflow-hidden">
              <h1 className={`text-4xl lg:text-5xl font-bold mb-6 leading-tight transition-all duration-700 ${isTransitioning ? 'translate-y-full opacity-0' : 'translate-y-0 opacity-100'}`}>
                {slides[current].headline}
              </h1>
              {/* Animated Underline */}
              <div className={`h-1 bg-gradient-to-r ${slides[current].gradient} rounded-full transition-all duration-700 ${isTransitioning ? 'scale-x-0' : 'scale-x-100'}`} style={{ width: '100%' }}></div>
            </div>

            {/* Subheadline with Staggered Animation */}
            <p className={`text-lg mb-8 text-[#c4c1c2] leading-relaxed transition-all duration-700 delay-200 ${isTransitioning ? 'translate-y-8 opacity-0' : 'translate-y-0 opacity-100'}`}>
              {slides[current].subheadline}
            </p>

            {/* Buttons with Enhanced Hover Effects */}
            <div className={`flex flex-col sm:flex-row gap-4 transition-all duration-700 delay-300 ${isTransitioning ? 'translate-y-8 opacity-0' : 'translate-y-0 opacity-100'}`}>
              <button className="group relative bg-white text-[#67595e] px-8 py-4 rounded-lg font-semibold hover:bg-[#f5f5f5] transition-all duration-300 shadow-lg overflow-hidden">
                <span className="relative z-10">Explore Our Services</span>
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-600 transform scale-x-0 group-hover:scale-x-100 transition-transform duration-300 origin-left"></div>
              </button>
              <button className="group relative border-2 border-white text-white px-8 py-4 rounded-lg font-semibold hover:bg-white hover:text-[#67595e] transition-all duration-300 shadow-lg overflow-hidden">
                <span className="relative z-10">View Projects</span>
                <div className="absolute inset-0 bg-white transform scale-x-0 group-hover:scale-x-100 transition-transform duration-300 origin-left"></div>
              </button>
            </div>

            {/* Advanced Slider Navigation */}
            <div className="flex items-center gap-4 mt-8">
              {/* Progress Bar */}
              <div className="flex-1 h-1 bg-white/20 rounded-full overflow-hidden">
                <div 
                  className={`h-full bg-gradient-to-r ${slides[current].gradient} rounded-full transition-all duration-500`}
                  style={{ width: `${((current + 1) / slides.length) * 100}%` }}
                ></div>
              </div>

              {/* Navigation Dots */}
              <div className="flex gap-3">
                {slides.map((_, idx) => (
                  <button
                    key={idx}
                    className={`group relative w-4 h-4 rounded-full transition-all duration-300 ${current === idx ? 'bg-white scale-125' : 'bg-white/30 hover:bg-white/60'}`}
                    onClick={() => handleSlideChange(idx)}
                    aria-label={`Go to slide ${idx + 1}`}
                  >
                    <div className={`absolute inset-0 rounded-full bg-gradient-to-r ${slides[idx].gradient} opacity-0 group-hover:opacity-100 transition-opacity duration-300`}></div>
                  </button>
                ))}
              </div>

              {/* Slide Counter */}
              <div className="text-sm text-white/70 font-medium">
                {current + 1} / {slides.length}
              </div>
            </div>
          </div>

          {/* Right Section - Enhanced Visual Display */}
          <div className="relative">
            <div className={`bg-gradient-to-br from-[#67595e] to-[#5a4f53] rounded-2xl p-8 shadow-2xl backdrop-blur-sm transition-all duration-700 ${isTransitioning ? 'scale-95 opacity-70' : 'scale-100 opacity-100'}`}>
              <div className="aspect-[4/3] bg-gradient-to-br from-[#8b7d82] to-[#7a6e73] rounded-xl overflow-hidden shadow-2xl border-2 border-black relative group">
                {/* Animated Border */}
                <div className={`absolute inset-0 bg-gradient-to-r ${slides[current].gradient} rounded-xl opacity-0 group-hover:opacity-20 transition-opacity duration-500`}></div>

                <img 
                  src="/images/display-showcase.jpg" 
                  alt="Digital display showcase with multiple screens"
                  className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                  onError={(e) => {
                    const target = e.target as HTMLImageElement;
                    target.style.display = 'none';
                    target.parentElement!.innerHTML = `
                      <div class="w-full h-full flex items-center justify-center">
                        <div class="text-center">
                          <div class="flex justify-center space-x-4 mb-4">
                            <div class="w-12 h-12 bg-white/40 rounded-full flex items-center justify-center backdrop-blur-sm">
                              <span class="text-2xl">üçã</span>
                            </div>
                            <div class="w-12 h-12 bg-white/40 rounded-full flex items-center justify-center backdrop-blur-sm">
                              <span class="text-2xl">üåø</span>
                            </div>
                            <div class="w-12 h-12 bg-white/40 rounded-full flex items-center justify-center backdrop-blur-sm">
                              <span class="text-2xl">üíß</span>
                            </div>
                          </div>
                          <p class="text-white font-bold text-lg">Digital Display Showcase</p>
                          <p class="text-white/80 text-sm">Premium Visual Experience</p>
                        </div>
                      </div>
                    `;
                  }}
                />

                {/* Floating Elements */}
                <div className="absolute top-4 right-4 bg-white/10 backdrop-blur-sm rounded-lg px-3 py-1 text-xs font-semibold text-white border border-white/20">
                  Live Preview
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
