import React, { useState, useEffect } from 'react';

interface CarouselSlide {
  id: number;
  title: string;
  subtitle: string;
  description: string;
  imageType: 'landscape' | 'portrait' | 'dual';
  imageContent: string;
  gradient: string;
}

const HeroCarousel: React.FC = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isPlaying, setIsPlaying] = useState(true);

  const slides: CarouselSlide[] = [
    {
      id: 1,
      title: "Leading LED Display",
      subtitle: "Solutions",
      description: "We specialize in high-quality LED displays, digital signage, and visual solutions that transform spaces and captivate audiences. From indoor displays to outdoor billboards, we deliver cutting-edge technology with exceptional service.",
      imageType: 'dual',
      imageContent: 'Digital Displays',
      gradient: "from-blue-900 to-blue-700"
    },
    {
      id: 2,
      title: "Perfect Reality",
      subtitle: "for Your Business",
      description: "Experience stunning visual clarity with our advanced LED technology. From retail environments to corporate spaces, our displays create immersive experiences that engage and inspire.",
      imageType: 'landscape',
      imageContent: 'Fresh & Dynamic Content',
      gradient: "from-gray-900 to-gray-700"
    },
    {
      id: 3,
      title: "Innovative",
      subtitle: "Visual Solutions",
      description: "Transform any space with our cutting-edge display technology. From interactive installations to large-scale outdoor displays, we bring your vision to life with unmatched quality and reliability.",
      imageType: 'portrait',
      imageContent: 'Premium Content Display',
      gradient: "from-purple-900 to-purple-700"
    }
  ];

  useEffect(() => {
    if (!isPlaying) return;

    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [isPlaying, slides.length]);

  const goToSlide = (index: number) => {
    setCurrentSlide(index);
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const renderDisplayImage = (slide: CarouselSlide) => {
    switch (slide.imageType) {
      case 'dual':
        return (
          <div className="grid grid-cols-2 gap-4 h-full">
            {/* Landscape Display */}
            <div className="bg-gradient-to-br from-blue-400 to-blue-600 rounded-lg p-4 shadow-lg">
              <div className="aspect-video bg-gradient-to-br from-yellow-400 to-green-400 rounded-lg flex items-center justify-center relative overflow-hidden">
                <div className="absolute inset-0 bg-gradient-to-br from-yellow-300 to-green-300 opacity-80"></div>
                <div className="relative z-10 text-center">
                  <div className="w-16 h-16 bg-white/20 rounded-full mx-auto mb-2 flex items-center justify-center">
                    <span className="text-2xl">üçã</span>
                  </div>
                  <p className="text-white font-semibold text-sm">Fresh Content</p>
                </div>
              </div>
            </div>

            {/* Portrait Display */}
            <div className="bg-gradient-to-br from-purple-400 to-purple-600 rounded-lg p-4 shadow-lg">
              <div className="aspect-[3/4] bg-gradient-to-br from-green-400 to-blue-400 rounded-lg flex items-center justify-center relative overflow-hidden">
                <div className="absolute inset-0 bg-gradient-to-br from-green-300 to-blue-300 opacity-80"></div>
